/*Imagine a publishing company that markets both book and audiocassette. The company uses the publication class to store the title, and price of a publication.The publication class is extended by two other classes: book and tape. The book class contains the member variable page_count, and the tape class contains the member variableplaying_time (in minutes). Ask the user to fill in data with getdata( ) and then display the data with putdata( ). Create m book objects and n tape objects. Display count of books that hasmore than 500 pages and print their details of the books. Accept a playing time in minutes from the user say X and display the title of the tapes that contain the playing time below X.*/import java.util.Scanner;class publication{     String title;	 double price;	 Scanner input =new Scanner(System.in);	 void getdata1()	 {	     System.out.println("Enter the title :");		 title =input.next();		 System.out.println("Enter the price :");		 price =input.nextDouble();	 }}class book extends publication{     int page_count;	 	 void getdata2()	 {	     System.out.println("Enter the number of pages of the book :");		 page_count=input.nextInt();	 }	 void putdata()	 {	     System.out.println("Title of the book       : "+title);		 System.out.println("Price of the book       : "+price);		 System.out.println("Number of pages in book : "+page_count);	 } 	 int return_pages()    {         return page_count;    }}class tape extends publication{     int playing_time;	 	 void getdata3()	 {	     System.out.println("Enter the number of minutes  of playing time :");		 playing_time =input.nextInt();	 }	 	 void putdata()	 {	     System.out.println("Title of the book                 : "+title);		 System.out.println("Price of the book                 : "+price);		 System.out.println("Number of minutes of playing time : "+playing_time);	 }	 int return_time()     {        return playing_time;     }}class publicationmain{     public static void main(String args[])	 {	     Scanner input =new Scanner(System.in);		 int m,n,x;		 System.out.println("Enter the number of books");		 m =input.nextInt();		 System.out.println("Enter the number of tapes");		 n =input.nextInt();		 book b[] =new book[m];		 tape t[] =new tape[n];		 		 for(int i=0;i<m;i++)		 {		      b[i] =new book();			 System.out.println("Enter the details of book "+(i+1));			 b[i].getdata1();			 b[i].getdata2();			 System.out.println();		 }		 System.out.println();		 System.out.println();		 		 for(int i=0;i<n;i++)		 {		      t[i] =new tape();			 System.out.println("Enter the details of tape "+(i+1)); 			 t[i].getdata1();			 t[i].getdata3();			 System.out.println();		 }		 System.out.println();		 System.out.println();		 		 for(int i=0;i<m;i++)		 {			 System.out.println("Details of book "+(i+1)+" are :");		     b[i].putdata();		 }		  System.out.println("******************************");		 		 for(int i=0;i<n;i++)		 {			 System.out.println("Details of tape "+(i+1)+" are :");		     t[i].putdata();		 }		 System.out.println("******************************");		 System.out.println("Details of books whose pages are more than 500 are :");		 for(int i=0;i<m;i++)		 {			  		     if(b[i].return_pages()>500)			 { 			     b[i].putdata();				  System.out.println();			 }		 }		 System.out.println("******************************");		 System.out.println("Enter the playing time in minutes");		 x =input .nextInt();		 System.out.println("Title of tapes whose playing time is less than "+x+" are :");		 for(int i=0;i<n;i++)		 {		     if(t[i].return_time()<x)			 {				  			     System.out.println(t[i].title);			 }		 }	 }}